		}

		// Function to search array
		public static function arraySearch( $array, $column, $value ) {
			if ( ! is_array( $array ) ) {
				return false;
			}
			foreach ( $array as $key => $innerArray ) {
				$exists = isset( $innerArray[ $column ] ) && $innerArray[ $column ] == $value;
				if ( $exists ) {
					return $key;
				}
			}

			return false;
		}
